mapper.select:
SELECT t1.parent_comment_id AS parentCommentId, t1.id AS commentId, t1.user_id AS userId, t1.content, t1.create_time AS createTime, t1.child_comment_count AS childCommentCount FROM ( SELECT id, parent_comment_id, user_id, content, create_time, COUNT(*) OVER (PARTITION BY parent_comment_id) AS child_comment_count FROM tbl_comment WHERE reply_to_user_id IS NULL AND parent_comment_id IN
#{parentCommentId}
) t1 INNER JOIN ( SELECT id FROM ( SELECT id, ROW_NUMBER() OVER ( PARTITION BY parent_comment_id ORDER BY IF(user_id = #{authorId}, 1, 2), create_time ) AS row_num FROM tbl_comment WHERE reply_to_user_id IS NULL AND parent_comment_id IN
#{parentCommentId}
) t WHERE row_num = 1 ) t2 ON t1.id = t2.id
